import React, { useState } from 'react';
import Icon from '../../components/Icon/Icon';

import './Prompt.css';

import Button from '../../components/Button/Button';
import TextField from '../../components/TextField/TextField';

export default function Prompt({ title, content, onSubmit, onCancel }) {

    var [value, setValue] = useState('');

    const onChange = (e) => {
        setValue(e.target.value);
        if (value.length > 30) {
            return;
        }
    }

    const onKeyDown = (e) => {
        if (e.key === 'Enter') {
            if(value.length < 1 || value.length > 36) {
                return;
            }
            onSubmit(value);
        }
    }

    return(
        <div class="prompt-container">
            <div class="prompt">
                <div class="prompt-content">
                    <h4 class="prompt-title">{title}</h4>
                    <TextField value={value} onChange={onChange} onKeyDown={onKeyDown} placeholder={content} minLength={1} maxLength={36} />

                    <div class="prompt-footer">
                        <Button onClick={() => onCancel()} color="red">Cancel</Button>
                        <Button onClick={() => onSubmit(value)} color="green">Submit</Button>
                    </div>
                </div>
            </div>
        </div>
    )
}
