import React from "react";
import "./SidePanel.css";
import Item from "./Item/Item";
import Icon from "../../components/Icon/Icon";


export default class SidePanel extends React.Component {
    constructor(props) {
        super(props);
    }

    onClick = (channel) => {
        console.log(channel);
    }

    newChannel = () => {
        this.props.onNewChannel();
    }

    render() {
      console.log(Object.entries(this.props.list));

        return (
          <div className="side-panel-container">
            <div className="side-panel">
                <div class="side-panel-header">
                </div>
                <div class="side-panel-list">
                  {Object.entries(this.props.list).map((item, index) => {
                      return (
                        <Item key={index} title={item.name} onClick={() => this.onClick(item)}/>
                      );
                    })
                  }
                </div>


                <Item isAdd={true} title="Create Channel" onClick={this.newChannel}/>
            </div>
          </div>
        );
    }
}
