import React from 'react';

import './FormInput.css';

export default class Input extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            message: ''
        };
    }

    onChange = (event) => {
        this.setState({
            message: event.target.value
        });
    }

    onSubmit = () => {
        this.props.onSubmit(this.state.message);
        this.setState({
            message: ''
        });
    }

    render() {
        return (
          <div className="chat-form-input-container">
            <input class="chat-form-input" type="text" placeholder={"Saisir le message..."} value={this.state.message} onChange={this.onChange}/>
            <button className="chat-form-submit-btn" onClick={this.onSubmit}>v</button>
          </div>
        );
    }
}
