import React from 'react';
import Input from './FormInput/FormInput';

import './ChatContent.css';
import Message from './Message/Message';

export default class ChatContent extends React.Component {

    renderMessages = () => {
      return this.props.messages.map((message, index) => {
            return (
              <Message key={index} username={"User"} content={message}/>
            );
          });
    }

    render() {
        return (
          <div className="chat-content-container">
            <div className="chat-content">
              <div className="chat-content-header">
                <h3 className="chat-content-title">Chat Content</h3>
              </div>
              <div className="chat-content-messages">
                {this.renderMessages()}
              </div>
              <div className="chat-content-footer-input">
                <Input onSubmit={this.props.onSubmit}/>
              </div>
            </div>
          </div>
        );
    }
}
