import Icon from '../../components/Icon/Icon';
import './Overlay.css';

export default function Overlay({ onSubmit }) {

    const onKeyDown = (e) => {
        if (e.key === 'Enter') {
            const value = e.target.value;

            if(e.target.minLength && e.target.maxLength && (value.length >= e.target.maxLength || value.length < e.target.minLenght)) {
                return;
            }
            onSubmit(value);
        }
    }

    return(
        <div class="overlay-container">
            <div class="overlay-content">
                <h4 class="overlay-title">Bienvenue sur le chat</h4>
                <div class="overlay-textfield">
                    <input class="overlay-input-content" type="text" onKeyDown={onKeyDown} placeholder='Saisir votre nom' minLenght={3} maxLength={30}/>
                    <button class="overlay-submit-btn" onClick={() => onSubmit('')}>
                        <Icon name="arrow_forward" />
                    </button>
                </div>
            </div>
        </div>
    )
}
