import React, { useState } from 'react';

import SidePanel from './SidePanel/SidePanel';
import ChatContent from './ChatContent/ChatContent';

import './App.css';
import './Fonts.css';


import Client from '../api/connection';

const client = new Client();


class Channel {
  constructor(path) {
    this.path = path;
    this.messages = [];
  }

  addMessage(message) {
    this.messages.push(message);
  }
}

class App extends React.Component {

  constructor(props) {
    super(props);

    this.state = {
      channels: {},
      currentPath: null,
    }

    client.connect();
    client.onMessage((data) => {

      this.addMessage("123", data)
    })
  }

  addMessage(channelPath, message) {
    var nextChannel = this.state.channels;

    for(const key in nextChannel) {
      if(key === channelPath) {
        nextChannel[key].addMessage(message);
      }
    }

    console.log("---");

    this.setState({
      channels: nextChannel
    });
  }

  sendMessage(channelPath, message) {
    this.addMessage(channelPath, message);

    client.sendMessage(channelPath, message);
  }

  addChannel() {
    const channelPath = prompt("Enter channel path");
    client.joinChannel(channelPath);

    this.setState({
      channels: {
        ...this.state.channels,
        [channelPath]: new Channel(channelPath)
      }
    });
  }

  changeChannel(channel) {
    console.log("Change channel", channel);

    this.setState({
      currentPath: channel.path
    });
  }

  render() {
    console.log(this.state.channels, this.state.currentPath);

    return (
      <div className="app-container">
        <div className="app-content">
          <SidePanel list={this.state.channels} onNewChannel={this.addChannel.bind(this)} onChangeChannel={this.changeChannel.bind(this)}/>

          <ChatContent channel={this.state.channels[this.state.currentPath] ?? null} onSubmit={this.sendMessage.bind(this)}/>
        </div>
      </div>
    );
  }


}

export default App;
