import './Overlay.css';

export default function Overlay({ onSubmit }) {

    const onKeyDown = (e) => {
        if (e.key === 'Enter') {
            onSubmit(e.target.value);
        }
    }

    return(
        <div class="overlay-container">
            <div class="overlay-content">
                <h3>Saisir votre nom</h3>
                <input type="text" onKeyDown={onKeyDown}/>
            </div>
        </div>
    )
}
