import React, { useState } from 'react';

import SidePanel from './SidePanel/SidePanel';
import ChatContent from './ChatContent/ChatContent';

import './App.css';
import './Fonts.css';


import Client from '../api/connection';

function App() {

  const client = new Client();
  client.connect();

  const [value, setValue] = useState([]);

  const [chanelList, setChanelList] = useState([]);

  function sendMessage(message) {
    console.log(message);
    setValue([...value, message]);
  }

  function addChanel(channel) {
    setChanelList([...chanelList, channel]);
  }


  return (
    <div className="app-container">
      <div className="app-content">
        <SidePanel list={chanelList} onNewChanel={addChanel}/>

        <ChatContent messages={value} onSubmit={sendMessage}/>
      </div>
    </div>
  );
}

export default App;
