import './Overlay.css';

export default function Overlay({ onSubmit }) {

    const onKeyDown = (e) => {
        if (e.key === 'Enter') {
            onSubmit(e.target.value);
        }
    }

    return(
        <div class="overlay-container">
            <div class="overlay-content">
                <h4 class="overlay-title">Bienvenue sur le chat</h4>
                <input class="overlay-input-content" type="text" onKeyDown={onKeyDown} placeholder='Saisir votre nom'/>
            </div>
        </div>
    )
}
