import React, { useState } from 'react';
import Icon from '../../components/Icon/Icon';

import './Prompt.css';

export default function Prompt({ title, content, onSubmit, onCancel }) {

    var [value, setValue] = useState('');

    const onChange = (e) => {
        setValue(e.target.value);
        if (value.length > 30) {
            return;
        }
    }

    const onKeyDown = (e) => {
        if (e.key === 'Enter') {
            if(value.length < 1 || value.length > 36) {
                return;
            }
            onSubmit(value);
        }
    }

    return(
        <div class="prompt-container">
            <div class="prompt">
                <div class="prompt-content">
                    <h4 class="prompt-title">{title}</h4>
                    <div class="prompt-textfield">
                        <input class="prompt-input-content" type="text" onKeyDown={onKeyDown} onChange={onChange} placeholder='Type your name' minLenght={3} maxLength={30}/>
                    </div>

                    <div class="prompt-buttons">
                        <button class="prompt-submit-btn" onClick={() => onSubmit(value)}>
                            <Icon name="arrow_forward" />
                        </button>

                        <button class="prompt-cancel-btn" onClick={onCancel}>
                            <Icon name="close" />
                        </button>
                    </div>
                </div>
            </div>
        </div>
    )
}
